    // 设置地址，该地址来自onBrodacastReceiver()函数阻塞数据报，datagramPacket.getAddress()  
    datagramPacket.setAddress(address);  
    // 发送的端口号  
    datagramPacket.setPort(8082);  
    try {  
        // 开始发送  
        multicastSocket.send(datagramPacket);  
    } catch (IOException e) {  
        e.printStackTrace();  
    }  
}  

